PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE filter_conditions (
                    filter_id TEXT PRIMARY KEY,
                    filter_name TEXT,
                    filter_url TEXT,
                    conditions_json TEXT,
                    field_mappings TEXT,
                    created_at TEXT,
                    updated_at TEXT,
                    last_synced TEXT
                );
CREATE TABLE field_definitions (
                    field_id TEXT PRIMARY KEY,
                    field_name TEXT,
                    field_key TEXT,
                    field_type TEXT,
                    options_json TEXT,
                    created_at TEXT,
                    updated_at TEXT
                );
COMMIT;
